import useStats from '../utils/useStats';
import styled from 'styled-components';

export default function Stats({ url, selected }) {
  const stats = useStats(url);
  if (!stats) return <p>Loading...</p>;
  console.log(stats);
  return (
    <Wrapper>
      <h2>{selected} STATS</h2>
      <div className='statBlock'>
        <h3>Confirmed Cases:</h3>
        <span>{stats.confirmed ? stats.confirmed.value : 'N/A'}</span>
      </div>
      <div className='statBlock'>
        <h3>Deaths:</h3>
        <span>{stats.deaths ? stats.deaths.value : 'N/A'}</span>
      </div>
      <div className='statBlock'>
        <h3>Recovered:</h3>
        <span>{stats.recovered ? stats.recovered.value : 'N/A'}</span>
      </div>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  h2 {
    font-size: 2.7rem;
    margin: 2rem;
    text-transform: uppercase;
  }
  h3 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
  }
  span {
    font-size: 4rem;
  }
  .statBlock {
    font-family: 'Roboto Condensed';
    margin: 1rem auto;
    padding: 2rem 3rem;
    background: white;
    max-width: 400px;
    border-radius: 0.5rem;
    box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
  }
`;
