import styled from 'styled-components';

export default function Layout({ children }) {
  return (
    <Wrapper>
      <header>
        <h1>COVID-19 Stats</h1>
      </header>
      <div>{children}</div>
      <footer>Tester</footer>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  min-height: 100vh;
  background: var(--lightColor);
  color: var(--darkColor);
  margin: 0;
  padding: 0;
  header {
    background: white;
    padding: 1rem;
    text-align: center;
  }
  h1 {
    color: var(--mainColor);
  }
`;
