import useStats from '../utils/useStats';
import styled from 'styled-components';

export default function Stats({ url }) {
  const stats = useStats(url);
  if (!stats) return <p>Loading...</p>;
  console.log(stats);
  return (
    <Wrapper>
      <h2>WORLDWIDE STATS</h2>
      <div className='statBlock'>
        <h3>Confirmed Cases:</h3>
        <span>{stats.confirmed ? stats.confirmed.value : 'N/A'}</span>
      </div>
      <div className='statBlock'>
        <h3>Deaths:</h3>
        <span>{stats.deaths ? stats.deaths.value : 'N/A'}</span>
      </div>
      <div className='statBlock'>
        <h3>Recovered:</h3>
        <span>{stats.recovered ? stats.recovered.value : 'N/A'}</span>
      </div>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  h2 {
    font-size: 2rem;
  }
  h3 {
    font-size: 1.8rem;
  }
  .statBlock {
    font-family: 'Roboto Condensed';
    margin: 1rem;
    padding: 1rem;
    background: white;
  }
`;
