import useStats from '../utils/useStats';
import styled from 'styled-components';

export default function CountrySelector({ setUrl }) {
  const countries = useStats('https://covid19.mathdro.id/api/countries');
  if (!countries) return <p>Loading...</p>;
  console.log(countries);

  return (
    <Wrapper>
      <select
        onChange={e => {
          e.target.value === 'all'
            ? setUrl('https://covid19.mathdro.id/api/')
            : setUrl(
                `https://covid19.mathdro.id/api/countries/${e.target.value}`
              );
        }}
      >
        <option value='all'>Worldwide</option>
        {Object.entries(countries.countries).map(([country, code]) => {
          return (
            <option value={code} key={country}>
              {country}
            </option>
          );
        })}
      </select>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  select {
    width: 100%;
    padding: 1rem;
    font-family: 'Montserrat';
    font-size: 1.8rem;
    outline: none;
    border: none;
  }
`;
